    const url = "http://10.199.128.20:8282/api/v1/fetchuserbyserial";
    const data = {
      serial: serial
    };
    try {
      const response = await fetch(url, {
        method: "POST", // or 'PUT'
        body: JSON.stringify(data), // data can be `string` or {object}!
        headers: {
          "Content-Type": "application/json"
        }
      });

