import React from "react";
import DesktopLayout from "../components/WebDesktop/DesktopLayout";
import Link from "next/link";
import Card from "../components/card";
import { subscribe, remove } from "../business/state";
import transactionBusiness from "../business/transactionBusiness";

class Index extends React.Component {
  constructor(props) {
    super(props);

  }

  render() {
    return (
      <DesktopLayout>
        <User />
      </DesktopLayout>
    );
  }
}

class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = { user: {} };
  }

  async getData(serial){
    var user = await transactionBusiness.fetchUserBySerial(serial);    
    console.log(user);
    this.setState({ user });
  }

  componentDidMount() {
    if (!this.idsubscribe){
      this.idsubscribe =     subscribe("onSearch", (serial) => {
      this.getData(serial);
    })
    }

  }

  componentWillUnmount() {
    remove("onSearch", this.idsubscribe);
  }

  render() {



    return (
      <React.Fragment>
        <div>Comercial Name: {this.state.user.comercialName} </div>
        <div>Comercial Name: {this.state.user.comercialName} </div>
        <div>Comercial Name: {this.state.user.comercialName} </div>
        <div>Comercial Name: {this.state.user.comercialName} </div>
        <div>Comercial Name: {this.state.user.comercialName} </div>
        <div>Comercial Name: {this.state.user.comercialName} </div>
        <div>Merchant Code: <input></input></div>
        <div><button>Actualizar</button></div>
      </React.Fragment>
    );
  }
}



export default Index;
